// Input: webhook JSON payload
// Output: path, content_base64, commit_message, canonical + passthrough config
function ensureString(v) { return typeof v === 'string' ? v : (v == null ? '' : JSON.stringify(v)); }

const data = items[0].json;

// Required fields validation
const required = ['title', 'slug', 'content_html', 'meta', 'meta.title', 'meta.description'];
function pick(path) { return path.split('.').reduce((o,k)=> (o||{})[k], data); }
for (const key of required) { if (!pick(key) || (typeof pick(key) === 'string' && pick(key).trim() === '')) { throw new Error(`Missing required field: ${key}`); } }

// Template (embedded)
const tpl = `<!doctype html>
<html lang="it">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>{{meta.title}}</title>
  <meta name="description" content="{{meta.description}}" />
  <meta name="keywords" content="{{meta.keywords}}" />
  <link rel="canonical" href="{{canonical}}" />
  <meta property="og:type" content="article" />
  <meta property="og:title" content="{{meta.title}}" />
  <meta property="og:description" content="{{meta.description}}" />
  <meta property="og:url" content="{{canonical}}" />
  <meta property="og:image" content="{{cover.url}}" />
  <meta name="twitter:card" content="summary_large_image" />
  <meta name="twitter:title" content="{{meta.title}}" />
  <meta name="twitter:description" content="{{meta.description}}" />
  <meta name="twitter:image" content="{{cover.url}}" />
  <link rel="preconnect" href="https://www.googletagmanager.com" crossorigin>
  <link rel="preconnect" href="https://www.google-analytics.com" crossorigin>
  <link rel="preconnect" href="https://cdn.jsdelivr.net" crossorigin>
  <link rel="preconnect" href="https://cdnjs.cloudflare.com" crossorigin>
  <script async src="https://www.googletagmanager.com/gtag/js?id={{GA_MEASUREMENT_ID}}"></script>
  <script>
    window.dataLayer = window.dataLayer || [];
    function gtag(){dataLayer.push(arguments);} 
    gtag('js', new Date());
    gtag('config', '{{GA_MEASUREMENT_ID}}', { 'anonymize_ip': true });
  </script>
  <script type="application/ld+json">{{schema_jsonld}}</script>
  <script defer src="/static/js/tracking.js"></script>
  <style>body { font-family: system-ui, -apple-system, Segoe UI, Roboto, sans-serif; line-height: 1.6; color: #0f172a; } .container { max-width: 1100px; margin: 0 auto; padding: 24px; } article img { max-width: 100%; height: auto; }</style>
</head>
<body>
  <main id="content" class="container">{{content_html}}</main>
</body>
</html>`;

const keywords = Array.isArray(data.meta?.keywords) ? data.meta.keywords.join(', ') : ensureString(data.meta?.keywords || '');
const schemaJson = typeof data.schema_jsonld === 'string' ? data.schema_jsonld : JSON.stringify(data.schema_jsonld || {}, null, 2);

function replaceAll(t, find, replace) { return t.split(find).join(replace); }
let html = tpl;
html = replaceAll(html, '{{meta.title}}', ensureString(data.meta.title));
html = replaceAll(html, '{{meta.description}}', ensureString(data.meta.description));
html = replaceAll(html, '{{meta.keywords}}', keywords);
html = replaceAll(html, '{{canonical}}', ensureString(data.canonical || `{{$json.baseUrlDomain}}/pages/blog/${data.slug}.html`));
html = replaceAll(html, '{{cover.url}}', ensureString(data.cover?.url || ''));
html = replaceAll(html, '{{schema_jsonld}}', schemaJson);
html = replaceAll(html, '{{content_html}}', ensureString(data.content_html));
// GA ID from env if present (n8n supports $env in expressions, but here we allow passthrough)
const gaId = data.GA_MEASUREMENT_ID || '';
html = replaceAll(html, '{{GA_MEASUREMENT_ID}}', gaId);

const path = `pages/blog/${data.slug}.html`;
const commitMessage = data.commit_message || `feat(blog): publish ${data.slug}`;
const contentBase64 = Buffer.from(html, 'utf8').toString('base64');

return [{ json: { ...items[0].json, path, content_base64: contentBase64, commit_message: commitMessage } }];

const input = items[0].json;
let sha = undefined;
// If previous node returned 200, it will include sha
if (Array.isArray(items[0].pairedItem)) { }
const prev = $items("GitHub GET file (sha)", 0, 0).json;
if (prev && prev.sha) { sha = prev.sha; }
const body = { message: input.commit_message, content: input.content_base64, branch: input.branch || 'main' };
if (sha) body.sha = sha;
return [{ json: { ...input, requestBody: body } }];
