/* IT-ERA Enhanced Design System */

/* Typography Enhancements */
.it-era-typography {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

.it-era-h1-enhanced {
    font-size: 2.5rem;
    font-weight: 700;
    line-height: 1.2;
    color: #111827;
    margin-bottom: 2rem;
    background: linear-gradient(135deg, #1e40af, #3b82f6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* Section Enhancements */
.it-era-section-enhanced {
    padding: 3rem 0;
    position: relative;
}

.it-era-section-enhanced:not(:last-child)::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 80%;
    height: 1px;
    background: linear-gradient(90deg, transparent, #e5e7eb, transparent);
}

/* Button Enhancements */
.it-era-btn-enhanced {
    background: linear-gradient(135deg, #1e40af, #3b82f6);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 8px;
    font-weight: 600;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    transition: all 0.3s ease;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.it-era-btn-enhanced:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    background: linear-gradient(135deg, #3b82f6, #1e40af);
}

/* Responsive Container */
.it-era-container-responsive {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1.5rem;
}

@media (max-width: 768px) {
    .it-era-container-responsive {
        padding: 0 1rem;
    }
}

/* Responsive Grid */
.it-era-grid-responsive {
    display: grid;
    gap: 2rem;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
}

@media (max-width: 768px) {
    .it-era-grid-responsive {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }
}

/* Brand Colors */
.it-era-brand-colors {
    --primary: #1e40af;
    --secondary: #3b82f6;
    --accent: #06b6d4;
    --success: #10b981;
    --warning: #f59e0b;
    --danger: #ef4444;
}

/* Navigation Enhancement */
.it-era-nav-enhanced {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid #e5e7eb;
    position: sticky;
    top: 0;
    z-index: 1000;
}

/* Icons Enhancement */
.it-era-icons-enhanced .service-item::before {
    content: 'üîß';
    font-size: 2rem;
    margin-bottom: 1rem;
    display: block;
}

/* Form Enhancement */
.it-era-form-enhanced {
    background: #f9fafb;
    padding: 3rem;
    border-radius: 12px;
    border: 1px solid #e5e7eb;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.it-era-form-enhanced input,
.it-era-form-enhanced textarea,
.it-era-form-enhanced select {
    width: 100%;
    padding: 1rem;
    border: 2px solid #d1d5db;
    border-radius: 6px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.it-era-form-enhanced input:focus,
.it-era-form-enhanced textarea:focus,
.it-era-form-enhanced select:focus {
    outline: none;
    border-color: #1e40af;
    box-shadow: 0 0 0 3px rgba(30, 64, 175, 0.1);
}

/* Mobile Optimizations */
@media (max-width: 768px) {
    .it-era-h1-enhanced {
        font-size: 2rem;
    }

    .it-era-section-enhanced {
        padding: 2rem 0;
    }

    .it-era-btn-enhanced {
        width: 100%;
        justify-content: center;
    }
}
